sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {
	//get the current date and time
	var rightNow = new GlideDateTime();

	var objNeedTasks = new GlideRecord( "x_58872_needit_needit_task" );
	objNeedTasks.addQuery( "u_due_date", '<=', rightNow );
	objNeedTasks.addQuery( "state", '<', 3 );
	objNeedTasks.addQuery( "assigned_to", current.assigned_to );

	objNeedTasks.query();

	while( objNeedTasks.next() ){
		// Print to the Message field on a notification
		var count = overdueNITasks.getRowCount();
		template.print("In addition to this overdue NeedIt Task, you have " + ( count - 1) + " more overdue NeedIt Tasks.");
	}


})(current, template, email, email_action, event);
