sysauto_script.script:
// take the current date and time
var rightNow = new GlideDateTime();

// get records which due soon for next 24 hours
var objTaskDueSoon = new GlideRecord( 'x_58872_needit_needit_task' );
objTaskDueSoon.addQuery( 'u_due_date', '>=', rightNow );
objTaskDueSoon.addQuery( 'u_due_date', '<', gs.hoursAgo(-24) );
objTaskDueSoon.addQuery( 'state' ,'!=', 3 );

//execute the query
objTaskDueSoon.query();

while( objTaskDueSoon.next() ){
	gs.info( 'NeedIt Task next due soon record = ' + objTaskDueSoon.number);
	gs.eventQueue( 'x_58872_needit.dueSoonNeedItTasks', objTaskDueSoon, objTaskDueSoon.number, gs.getUserName() );
}
